import{_ as l,c as i,o as a,V as e}from"./chunks/framework.35c8bad0.js";const m=JSON.parse('{"title":"面经[主观问题]","description":"","frontmatter":{"title":"面经[主观问题]","date":"2023-07-07T00:00:00.000Z"},"headers":[],"relativePath":"blog/interview/230707.md","lastUpdated":1706961084000}'),t={name:"blog/interview/230707.md"},o=e('<h1 id="面经-主观问题" tabindex="-1">面经[主观问题] <a class="header-anchor" href="#面经-主观问题" aria-label="Permalink to &quot;面经[主观问题]&quot;">​</a></h1><h2 id="_1-流程性问题" tabindex="-1">1. 流程性问题 <a class="header-anchor" href="#_1-流程性问题" aria-label="Permalink to &quot;1. 流程性问题&quot;">​</a></h2><ol><li>自我介绍</li><li>是否在职</li><li>离职原因</li><li>职业规划</li><li>未来团队期待</li><li>关注的前端方向</li><li>个人价值体现最大的地方</li><li>程序员除了提升技术能力之外，其它什么能力你比较看重？</li></ol><h2 id="_2-业务场景问题" tabindex="-1">2. 业务场景问题 <a class="header-anchor" href="#_2-业务场景问题" aria-label="Permalink to &quot;2. 业务场景问题&quot;">​</a></h2><ol><li>团队成员是怎么配合完成任务的？</li><li>印象深刻的问题、项目场景，并且怎么解决的？</li><li>团队4个人人，你是leader，有一个非常紧急且重要的任务，需要两周开发完整，你如何应对？</li></ol><h2 id="_3-其它问题" tabindex="-1">3. 其它问题 <a class="header-anchor" href="#_3-其它问题" aria-label="Permalink to &quot;3. 其它问题&quot;">​</a></h2><h3 id="说一下你喜欢什么样的领导" tabindex="-1">说一下你喜欢什么样的领导 <a class="header-anchor" href="#说一下你喜欢什么样的领导" aria-label="Permalink to &quot;说一下你喜欢什么样的领导&quot;">​</a></h3><ol><li>明确的愿景和目标：优秀的领导应该有清晰的愿景和明确的目标，并能够将其传达给团队成员，激发他们的动力和共同努力。</li><li>激励与赋能：优秀的领导应该具备激励和赋能团队成员的能力，激发他们的潜力、提供机会和资源，使他们能够取得更好的成果。</li><li>沟通和倾听能力：领导应该具备良好的沟通能力，能够有效传达信息、听取反馈和理解团队成员的需求和意见。</li><li>公正和公平：优秀的领导应该具备公正和公平的品质，对待团队成员一视同仁，建立公正的评价和奖励机制。</li><li>团队合作和协调：领导应该具备团队合作和协调的能力，激发团队成员间的合作精神，促进协作和信息共享。</li><li>知识和专业能力：领导应该具备相关领域的知识和专业能力，能够为团队提供指导和支持，并在必要时做出明智的决策。</li><li>激励个人成长：优秀的领导应该关注团队成员的个人成长，提供培训和发展机会，激励他们不断学习和进步。</li></ol><h2 id="_4-你有什么问题需要问我吗" tabindex="-1">4. 你有什么问题需要问我吗 <a class="header-anchor" href="#_4-你有什么问题需要问我吗" aria-label="Permalink to &quot;4. 你有什么问题需要问我吗&quot;">​</a></h2><h3 id="技术官" tabindex="-1">技术官 <a class="header-anchor" href="#技术官" aria-label="Permalink to &quot;技术官&quot;">​</a></h3><ol><li>公司前端部门现在多少人？</li><li>打算招几个前端？</li><li>公司前端用的技术栈有哪些？</li><li>入职后 我会在哪个部门负责哪个项目？</li><li>是外包公司吗？包的哪家公司的项目？</li><li>面试结果什么时候通知我？</li></ol><h3 id="hr" tabindex="-1">HR <a class="header-anchor" href="#hr" aria-label="Permalink to &quot;HR&quot;">​</a></h3><ol><li>最早到岗时间是什么时候？</li><li>除了工资外，每个月还有其他补贴吗？</li><li>试用期多久？试用期薪资怎么算？</li><li>社保和五险一金有吗？缴费基数和比例是？</li><li>周末双休吗？平时加班多不多？工作日每天大概几点下班？</li><li>平时加班和周末加班是否有加班费或者调休？</li><li>公司有没有健身房篮球场等设备吗？</li><li>电脑用自己的还是公司的？</li></ol>',13),r=[o];function h(n,d,s,_,c,u){return a(),i("div",null,r)}const q=l(t,[["render",h]]);export{m as __pageData,q as default};
